// Liste de tous les utilisateurs

import axios from 'axios';
import React from 'react';
import { Switch, BrowserRouter, Route, Link } from "react-router-dom";
import { Button, Nav, Table, Col, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


const User = props => (
	<tr>

		<td> {props.user.location}</td>
		<td> {props.user.personsInHouse}</td>
		<td> {props.user.houseSize}</td>
	</tr>
)


class extraitListe extends React.Component {


	constructor(props) {

		super(props);		
		this.state = {
			users: []
		};
	}

	componentDidMount(){
		axios.get('http://localhost:4000/User')
			.then(response => {
				this.setState({ users: response.data })
			})
			.catch((error) => { console.log(error) }); 
	}


	userList()
	{
		return this.state.users.map(currentUser =>
		{
			return <User user={currentUser} key={currentUser._id}/>;
		})
	}

	render() {
		return (
			<div className="container">

				<h3> Extrait de la liste utilisateurs</h3> 

				<Table striped>
			<thead className="thead-light">
				<tr>
					<th> Pays </th>
					<th> Nombre d'habitants </th>
					<th> Taille  </th>
				</tr>

			</thead>

			<tbody > 
			{this.userList()[0]}
			{this.userList()[1]}
			{this.userList()[2]}
			 </tbody>
			</Table>
			</div>
		);
	}


}




export default extraitListe;
