import React from 'react';
import Fragment from 'render-fragment';
import axios from 'axios';
import { Switch, BrowserRouter, Route, Link } from "react-router-dom";
import { Navbar, NavbarBrand, Nav, NavItem } from 'reactstrap';
import { Row, Col, Card, CardBody, CardTitle } from 'reactstrap';
import {
	Bar, BarChart, PieChart, Pie, Legend, Cell, Tooltip, Sector,
	Label, LabelList, XAxis, YAxis, LineChart, Line, CartesianGrid, ResponsiveContainer
} from 'recharts';
import extraitListe from './extraitListe.js';



const Data = props => (
	<tr>

		<td> {props.measure._id}</td>
		<td> {props.measure.count}</td>
	</tr>
)

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({
	cx, cy, midAngle, innerRadius, outerRadius, percent, index,
}) => {
	const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
	const x = cx + radius * Math.cos(-midAngle * RADIAN);
	const y = cy + radius * Math.sin(-midAngle * RADIAN);

	return (
		<text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
			{`${(percent * 100).toFixed(0)}%`}
		</text>
	);
};


class Homepage extends React.Component{


	constructor(props) {
		super(props);

		this.toggle = this.toggle.bind(this);


		this.state = {
			countryName:'',
			count:'',
			nbDocs:'',
			measureData:[],	
			isOpen: false
		};


		this.data = [{ name: 'Assiette Primeur', vente: 200 }, { name: 'Lasagne aux trois légumes', vente: 100 }, { name: 'Veggie burger', vente: 80 },];



		this.renderCustomAxisTick = ({ x, y, payload }) => {
			let path = '';

