// Formulaire pour ajouter un utilisateur


import React from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


class Formu extends React.Component {


	constructor(props){
		this.onChangeLocation = this.onChangeLocation.bind(this);
		this.onChangeNbPersons = this.onChangeNbPersons.bind(this);
		this.onChangeHouseSize = this.onChangeHouseSize.bind(this);
		this.onSubmit = this.onSubmit.bind(this);


		this.state = {
			location: '',
			personsInHouse:'',
			houseSize:'',
		}
	}

	onChangeLocation(e){
		this.setState({
			location: e.target.value
		});
	}

	onChangeNbPersons(e) {
		this.setState({
			personsInHouse: e.target.value
		});
	}
	onChangeHouseSize(e) {
		this.setState({
			houseSize: e.target.value
		});
	}
	onSubmit(e){
		e.preventDefault();
		const user = {
			location: this.state.location,
			personsInHouse: this.state.personsInHouse,
			houseSize: this.state.houseSize,
	};



	}

	render(){
		return (
			<Col lg="6" xl="3">
			<Form onSubmit={this.onSubmit}>
				<FormGroup>
					<Label for="location">Pays : </Label>
					<Input type="text" required value={this.state.location} onChangeLocation={this.onChangeLocation} placeholder=" " />
				</FormGroup>

				<FormGroup>
					<Label for="personsInHouse">Nb de personnes dans la maison : </Label>
						<Input type="select" value={this.state.personsInHouse} onChangeNbPersons={this.onChangeNbPersons} />
						<option>1</option>
						<option>2</option>
						<option>3</option>
						<option>4</option>
						<option>5</option>
				</FormGroup>

				<FormGroup>
						<Label for="houseSize">Taille de la maison : </Label>
						<Input type="text" required value={this.state.houseSize} onChangeHouseSize={this.onChangeHouseSize} placeholder=" " />
				</FormGroup>


			</Form>
			</Col>
			);
	}


}




export default Formu;
