// Formulaire pour ajouter un utilisateur

import axios from 'axios';
import React from 'react';
import { Button,  Col, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


class Formu extends React.Component {


	constructor(props){

		super(props);

		this.onChangeLocation = this.onChangeLocation.bind(this);
		this.onChangeNbPersons = this.onChangeNbPersons.bind(this);
		this.onChangeHouseSize = this.onChangeHouseSize.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
		this.onSubmitShow = this.onSubmitShow.bind(this);


		this.state = {
			location: '',
			personsInHouse:'',
			houseSize:'',
		}
	}



	onChangeLocation(e){
		this.setState({
			location: e.target.value
		});
	}

	onChangeNbPersons(e) {
		this.setState({
			personsInHouse: e.target.value
		});
	}
	onChangeHouseSize(e) {
		this.setState({
			houseSize: e.target.value
		});
	}

	onSubmit(e){
		e.preventDefault();
		const user = {
			location: this.state.location,
			personsInHouse: this.state.personsInHouse,
			houseSize: this.state.houseSize,


		};

		console.log(user);


		axios.post('http://localhost:4000/admin', user)
			.then(res => console.log(res.data));

		this.setState({
			location:'',
			personsInHouse:'',
			houseSize:''

		})



	}

	onSubmitShow(e) {
		e.preventDefault();
		const user = {
			location: this.state.location,
			personsInHouse: this.state.personsInHouse,
			houseSize: this.state.houseSize,


		};

		console.log(user);


		axios.get('http://localhost:4000/User')

			.then(response => {
				if (response.data.length > 0) {
					this.setState({

						location: response.data[0].location,
						personsInHouse: response.data[0].personsInHouse,
						houseSize: response.data[0].houseSize,

					})
				}
			})

	}




	render(){
		return (

			<div  className="container">


				<div className="header-body">
			<Form onSubmit={this.onSubmit}>
				<FormGroup>
					<Label for="location">Pays : </Label>
					<Input type="text"  value={this.state.location} onChange={this.onChangeLocation} placeholder=" " />
				</FormGroup>

				<FormGroup>
					<Label for="personsInHouse">Nb de personnes dans la maison : </Label>
							<Input type="select" value={this.state.personsInHouse} onChange={this.onChangeNbPersons} >
						<option >1</option>
						<option>2</option>
						<option>3</option>
						<option>4</option>
						<option>5</option>
						</Input>
				</FormGroup>

				<FormGroup>
						<Label for="houseSize">Taille de la maison : </Label>
							<Input type="text" value={this.state.houseSize} onChange={this.onChangeHouseSize} placeholder=" " />
				</FormGroup>

				<FormGroup>
				<Input type = "submit" value = "Ajouter un utilisateur " onChange={this.onSubmit} className="btn btn-primary"/>
				</FormGroup>




			</Form>

			<Form onSubmit={this.onSubmitShow}>

					<FormGroup>
						<Input type="submit" value="Afficher les utilisateurs" onChange={this.onSubmitShow} className="btn btn-info" />
						<Label > {this.state.houseSize} </Label>
						<Label > {this.state.personsInHouse} </Label>
						<Label > {this.Location} </Label>

					</FormGroup>

			</Form>

			</div>
			</div>
			);
	}


}




export default Formu;
