// Liste de tous les utilisateurs

// Formulaire pour ajouter un utilisateur

import axios from 'axios';
import React from 'react';
import { Button, Table, Col, Form, FormGroup, Label, Input, FormText } from 'reactstrap';





class Liste extends React.Component {


	constructor(props) {

		super(props);

		this.onChangeLocation = this.onChangeLocation.bind(this);
		this.onChangeNbPersons = this.onChangeNbPersons.bind(this);
		this.onChangeHouseSize = this.onChangeHouseSize.bind(this);
		this.onSubmit = this.onSubmit.bind(this);


		this.state = {
			location: '',
			personsInHouse: '',
			houseSize: '',
			users:[],
		}
	}

	getItems(){
		return this.state.users.map((item, index) => item.localisation);
	}

	componentDidMount() {

		axios.get('http://localhost:4000/User')

			.then(response => {
				if (response.data.length > 0) {
					this.setState({

						location: response.data[0].location,
						personsInHouse: response.data[0].personsInHouse,
						houseSize: response.data[0].houseSize,

					})
				}
			})
	}

	onChangeLocation(e) {
		this.setState({
			location: e.target.value
		});
	}

	onChangeNbPersons(e) {
		this.setState({
			personsInHouse: e.target.value
		});
	}
	onChangeHouseSize(e) {
		this.setState({
			houseSize: e.target.value
		});
	}

	onSubmit(e) {
		e.preventDefault();

		const user = {
			location: this.state.location,
			personsInHouse: this.state.personsInHouse,
			houseSize: this.state.houseSize,
		};
		user = this.getItems();

	}

	render() {
		return (

			<div className="container">


				<div className="header-body">

					<Table striped onSubmit={this.onSubmit} >
						<thead>
							<tr>
								<th>Utilisateur</th>
								<th>Pays</th>
								<th>Nombre de personnes</th>
								<th>Taille de l'habitation</th>
							</tr>
						</thead>
						<tbody >
							{this.state.users.map((item, index)=>
								<tr key={index}>
									<td className="text-left"> {item[0]} </td>
								</tr>
								)}

						</tbody>
					</Table>


				</div>
			</div>
		);
	}


}




export default Liste;
