// Formulaire pour ajouter un utilisateur


import React from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';


class Formu extends React.Component {


	constructor(props){
		this.onChangeLocation = this.onChangeLocation.bind(this);
		this.onChangeNbPersons = this.onChangeNbPerson.bind(this);
		this.onChangeHouseSize = this.onChangeHouseSize.bind(this);
		this.onSubmit = this.onSubmit.bind(this);


		this.state = {
			location: '',
			personsInHouse:'',
			houseSize:'',
		}
	}

	onChangeLocation(e){
		this.setState({
			location: e.target.value
		});
	}

	onChangeNbPersons(e) {
		this.setState({
			personsInHouse: e.target.value
		});
	}
	onChangeHouseSize(e) {
		this.setState({
			houseSize: e.target.value
		});
	}
	onSubmit(e){
		e.preventDefault();
		const user = {
			location: this.state.location,
			personsInHouse: this.state.personsInHouse,
			houseSize: this.state.houseSize,

	};



	}

	render(){
		return (

			<Form>
				<FormGroup>
					<Label for="nom">Nom</Label>
					<Input type="text" name="text" id="nom" placeholder=" " />
				</FormGroup>
				<FormGroup>
					<Label for="nom">Prénom</Label>
					<Input type="text" name="text" id="prenom" placeholder=" " />
				</FormGroup>
				<FormGroup>
					<Label for="num">Numéro de tel</Label>
					<Input type="number" id="num" placeholder=" " />
				</FormGroup>
				<FormGroup>
					<Label for="exampleSelect">Nombre de personnes</Label>
					<Input type="select" name="select" id="exampleSelect">
						<option>1</option>
						<option>2</option>
						<option>3</option>
						<option>4</option>
						<option>5</option>
					</Input>
				</FormGroup>
			</Form>
			);
	}


}




export default Formu;
